import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ListaCriptomonedas: React.FC = () => {
  const [criptomonedas, setCriptomonedas] = useState<{ [key: string]: number }>({});
  const [monto, setMonto] = useState('');
  const [criptoSeleccionada, setCriptoSeleccionada] = useState('');
  const [mensaje, setMensaje] = useState('');

  useEffect(() => {
    // Obtener la lista de criptomonedas desde la API del backend
    const obtenerCriptomonedas = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/cryptos/lista');
        setCriptomonedas(response.data);
      } catch (error) {
        console.error('Error al obtener criptomonedas', error);
      }
    };

    obtenerCriptomonedas();
  }, []);

  const handleInvertir = () => {
    if (!criptoSeleccionada || !monto) {
      setMensaje('Selecciona una criptomoneda y un monto');
      return;
    }

    // Aquí puedes hacer la lógica para actualizar el portafolio del usuario
    setMensaje(`Has invertido ${monto} USD en ${criptoSeleccionada}`);
  };

  return (
    <div>
      <h2>Lista de Criptomonedas</h2>
      <ul>
        {Object.keys(criptomonedas).map((simbolo) => (
          <li key={simbolo}>
            {simbolo}: ${criptomonedas[simbolo]}
            <button onClick={() => setCriptoSeleccionada(simbolo)}>Seleccionar</button>
          </li>
        ))}
      </ul>

      <h3>Invertir en {criptoSeleccionada}</h3>
      <input 
        type="number" 
        placeholder="Monto a invertir" 
        value={monto} 
        onChange={(e) => setMonto(e.target.value)} 
      />
      <button onClick={handleInvertir}>Invertir</button>
      <p>{mensaje}</p>
    </div>
  );
};

export default ListaCriptomonedas;
