import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface Portafolio {
  id: number;
  criptomoneda: string;
  cantidad_invertida: number;
  precio_compra: number;
}

const Home: React.FC = () => {
  const [user, setUser] = useState<{ nombre: string, saldo: number } | null>(null);
  const [portafolios, setPortafolios] = useState<Portafolio[]>([]);
  const [preciosActuales, setPreciosActuales] = useState<{ [key: string]: number }>({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      axios.get('http://localhost:5000/auth/me', {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(response => setUser(response.data))
      .catch(error => console.error('Error al obtener el usuario', error));

      axios.get('http://localhost:5000/api/portafolio/lista', {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(response => setPortafolios(response.data))
      .catch(error => console.error('Error al obtener portafolios', error));

      // Leer los precios actuales desde el localStorage
      const preciosGuardados = localStorage.getItem('preciosCriptos');
      if (preciosGuardados) {
        setPreciosActuales(JSON.parse(preciosGuardados));
      }
    }
  }, []);

  const calcularPorcentaje = (precioCompra: number, precioActual: number) => {
    return ((precioActual - precioCompra) / precioCompra * 100).toFixed(2);
  };

  const calcularGananciaPerdida = (cantidadInvertida: number, precioCompra: number, precioActual: number) => {
    return (cantidadInvertida * (precioActual - precioCompra)).toFixed(2);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div>
      <h2>Bienvenido a la Home</h2>
      {user ? (
        <>
          <p>Bienvenido, {user.nombre}!</p>
          <p>Tu saldo actual es: ${user.saldo}</p>

          <button onClick={() => navigate('/agregar-saldo')}>Agregar Saldo</button>
          <button onClick={() => navigate('/lista-criptomonedas')}>Ver Criptomonedas</button>

          <h3>Mis Inversiones:</h3>
          <ul>
            {portafolios.map((portafolio) => {
              const precioActual = preciosActuales[portafolio.criptomoneda];  
              if (!precioActual) return <li key={portafolio.id}>Cargando precios...</li>; 

              const porcentaje = calcularPorcentaje(portafolio.precio_compra, precioActual);
              const gananciaPerdida = calcularGananciaPerdida(portafolio.cantidad_invertida, portafolio.precio_compra, precioActual);

              return (
                <li key={portafolio.id}>
                  {portafolio.criptomoneda}: ${portafolio.cantidad_invertida}
                  <br />
                  Precio de compra: ${portafolio.precio_compra}
                  <br />
                  Precio actual: ${precioActual}
                  <br />
                  Ganancia/Pérdida: {porcentaje}% (${gananciaPerdida})
                </li>
              );
            })}
          </ul>

          <button onClick={handleLogout}>Cerrar sesión</button>
        </>
      ) : (
        <p>Cargando...</p>
      )}
    </div>
  );
};

export default Home;
