import React from 'react';
import { Navigate } from 'react-router-dom';

// Componente que protegerá las rutas
const ProtectedRoute = ({ children }: { children: JSX.Element }) => {
  const isAuthenticated = !!localStorage.getItem('token'); // Verificar si hay token

  if (!isAuthenticated) {
    return <Navigate to="/login" />; // Redirigir a login si no está autenticado
  }

  return children; // Mostrar la ruta si está autenticado
};

export default ProtectedRoute;
