import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Home.css';

// Define la interfaz para los portafolios
interface Portafolio {
  id: number;
  criptomoneda: string;
  cantidad_invertida: number; // Este es el monto en USD invertido
  precio_compra: number; // Precio de compra por unidad de la criptomoneda
}

const Home: React.FC = () => {
  const [user, setUser] = useState<{ nombre: string, saldo: number } | null>(null);
  const [portafolios, setPortafolios] = useState<Portafolio[]>([]);
  const [valorTotalUSD, setValorTotalUSD] = useState(0); // Para mostrar el total en dólares
  const [porcentajeTotal, setPorcentajeTotal] = useState(0); // Para mostrar el porcentaje total de ganancia/pérdida
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:5000/api/portafolio/lista', { headers: { Authorization: `Bearer ${token}` } })
        .then(response => {
          setPortafolios(response.data);
          updateTotals(response.data);
        })
        .catch(error => console.error('Error al obtener los portafolios', error));
    }
  }, []);

  const updateTotals = (portafolios: Portafolio[]) => {
    const preciosCriptos = localStorage.getItem('preciosCriptos');
    if (preciosCriptos) {
      const preciosActuales = JSON.parse(preciosCriptos);
      let totalInvertido = 0;
      let totalValorActual = 0;

      portafolios.forEach((portafolio: Portafolio) => {
        const precioActual = preciosActuales[portafolio.criptomoneda];
        if (precioActual && portafolio.precio_compra > 0) {
          const cantidadCriptos = portafolio.cantidad_invertida / portafolio.precio_compra;
          const valorEnDolares = cantidadCriptos * precioActual;

          totalInvertido += portafolio.cantidad_invertida;
          totalValorActual += valorEnDolares;
        }
      });

      if (totalInvertido > 0) {
        setValorTotalUSD(totalValorActual);
        setPorcentajeTotal(((totalValorActual - totalInvertido) / totalInvertido) * 100);
      } else {
        setValorTotalUSD(0);
        setPorcentajeTotal(0);
      }
    }
  };

  // Función para desloguearse
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="home-container">
    <div>
      <h2 className="welcome-message">Panel Principal</h2>
      {user ? (
        <>
          <h3 className="welcome-name">Bienvenido, {user.nombre}!</h3>
          <h3 className="important-info">Tu saldo actual es: ${user.saldo}</h3>

          {/* Mostrar el valor total en USD y el porcentaje de ganancia o pérdida */}
          <h3 className="important-info">
            Valor total de tus inversiones en USD: ${valorTotalUSD.toFixed(2)} 
            ({porcentajeTotal.toFixed(2)}%)
          </h3>

          {/* Botones */}
          <button onClick={() => navigate('/agregar-saldo')}>Agregar Saldo</button>
          <button onClick={() => navigate('/lista-criptomonedas')}>Ver Criptomonedas</button>
          <button onClick={() => navigate('/cashout')}>Ir a Cashout</button>{/* Botón para ir a Cashout */}
          <button onClick={() => navigate('/graficas')}>Ir a Gráficas</button>

          {/* Listar los portafolios del usuario */}
          <div className="investment-section">
            <h3>Mis Inversiones:</h3>
            {portafolios.map((portafolio) => {
              const preciosCriptos = localStorage.getItem('preciosCriptos');
              const preciosActuales = preciosCriptos ? JSON.parse(preciosCriptos) : {};
              const precioActual = preciosActuales[portafolio.criptomoneda];

              // Calcular la cantidad de criptomonedas basada en la inversión y el precio de compra
              let cantidadCriptos = portafolio.precio_compra > 0 ? 
                Number(portafolio.cantidad_invertida) / Number(portafolio.precio_compra) : 0;

              // Calcular la ganancia o pérdida porcentual
              let porcentajeGanancia = portafolio.precio_compra > 0 ? 
                ((Number(precioActual) - Number(portafolio.precio_compra)) / Number(portafolio.precio_compra)) * 100 : 0;

              const progressBarWidth = Math.abs(porcentajeGanancia);


              return (
                <div key={portafolio.id} className="investment-card">
                  <div className="investment-details">
                    <strong>{portafolio.criptomoneda}:</strong> {cantidadCriptos.toFixed(6)} unidades
                    <br />
                    Invertido: ${Number(portafolio.cantidad_invertida).toFixed(2)} a un precio de compra de ${Number(portafolio.precio_compra).toFixed(2)}
                    <br />
                    Valor actual: ${precioActual ? (cantidadCriptos * Number(precioActual)).toFixed(2) : 'Desconocido'}
                    <br />
                    <span className={porcentajeGanancia < 0 ? "negative" : ""}>
                      Ganancia/Pérdida: {porcentajeGanancia.toFixed(2)}%
                      <div className="progress-bar">
                        <div className="progress-bar-inner" style={{ width: `${progressBarWidth}%` }}></div>
                      </div>
                    </span>
                  </div>
                </div>
              );
            })}
          </div>

          <button onClick={handleLogout}>Cerrar sesión</button>
        </>
      ) : (
        <p>Cargando...</p>
      )}
    </div>
    </div>
  );
};

export default Home;
